<template>
  <div >
    <text>123{{url}}</text>
    <web ref="webview" style="width: 730px; height: 500px" :url="url"
      @pagestart="onPageStart"></web>
    <text>{{test}}223</text>
  </div>
</template>

<script>
import Utils from '../utils/index.js'
export default {
  name: 'am-html',
  props: {
    width: {
      type: Number,
      default: 750
    },
    height: {
      type: Number,
      default: 750
    },
    html: {
      type: String,
      default: ''
    }
  },
  data () {
    return {
      isWeb: Utils.isWeb(),
      test: ''
    }
  },
  computed: {
    url () {
      return `javascript:setTimeout(function () {
          alert('123')
          var meta = document.createElement('meta');
          meta.setAttribute('name', 'viewport');
          meta.setAttribute('content', 'width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no');
          document.head.appendChild(meta);
          document.body.innerHTML = '<style>body, html {margin: 0;}</style>${this.html}'
        });`
    }
  },
  methods: {
    onPageStart (e) {
      this.test = e.url
    }
  },
  created () {
    var modal = weex.requireModule('modal')
    modal.alert({
      message: weex.config.env.weexVersion,
      duration: 0.3
    }, function (value) {
      console.log('alert callback', value)
    })
  }
}
</script>
